import React from "react";
import Slider from "meteor/empirica:slider";
import { TimeSync } from "meteor/mizzao:timesync";
import moment from "moment";

import SymbolButton from "./SymbolButton.jsx";

export default class TaskResponse extends React.Component {

  handleSubmit = event => {
    const { stage, player, game } = this.props;

    event.preventDefault();
    console.log("submitted");
    stage.append("log", {
      verb: "playerSubmitted",
      subjectId: player._id,
      object: true,
      at: moment(TimeSync.serverTime(null, 1000)),
    })
    player.set("submitted", true);
    this.setState({submitted: true});
  };

  handleReconsider = event => {
    const { stage, player, game } = this.props;

    event.preventDefault();
    console.log("Reconsidering...");
    player.set("submitted", false);

    stage.append("log", {
      verb: "playerReconsidered",
      subjectId: player._id,
      object: true,
      at: moment(TimeSync.serverTime(null, 1000)),
    })
  }

  handleButtonSelect= (symbolName) => {
    const { stage, player, game } = this.props;
    stage.set("selectedButton", symbolName);

    stage.append("log", {
      verb: "selectingSymbol",
      subjectId: player._id,
      object: symbolName,
      at: moment(TimeSync.serverTime(null, 1000)),
    });

    console.log(stage.get("log"));

  }

  renderSubmitted() {
    return (
      <div className="task-response">
        <div className="response-submitted">
          <h5>Waiting on other players...</h5>
          Please wait until all players are ready. If you would like to reconsider your answer, click on the reconsider button.
        </div>
      </div>
    );
  }

  renderSlider() {
    const { player } = this.props;
    const value = player.round.get("value");
    return (
      <Slider
        min={0}
        max={1}
        stepSize={0.01}
        labelStepSize={0.25}
        onChange={this.handleChange}
        value={value}
        hideHandleOnEmpty
      />
    );
  }

  renderButton() {
    const { stage, player, game } = this.props;
    const task = stage.get("task");

    return (
      task.symbols.map((symbol) => 
      <SymbolButton 
        key={symbol}
        name={symbol}
        handleButtonSelect={(symbolName) => this.handleButtonSelect(symbolName)}
        stage={stage}
        game={game}
        player={player}
      />
      )
    );
  }

  render() {
    const { stage, player, game, handleReconsider } = this.props;

    const submitted = player.get("submitted");
    // If the player already submitted, don't show the slider or submit button
    console.log(submitted);

    const disabled = player.get("submitted");
    return (
      <div className="task-response">
        {submitted ? this.renderSubmitted() : ""}
        {this.renderButton()}
        <form onSubmit={this.handleSubmit}>
          {/* {submitted ?  */}
          <button disabled={submitted ? true : false} type="submit"> Submit </button> 
          {/* <button type="submit">Submit</button>
          } */}
        </form>
        {/* { !submitted ? */}
        <button disabled={!submitted ? true : false} onClick={this.handleReconsider}> Reconsider </button> 
        {/* <button onClick={this.handleReconsider}> Reconsider </button>
        } */}
      </div>
    );
  }
}
