import React from "react";
import Slider from "meteor/empirica:slider";

import SymbolButton from "./SymbolButton.jsx";

export default class TaskResponse extends React.Component {

  handleSubmit = event => {
    event.preventDefault();
    this.props.player.stage.submit();
  };

  handleButtonSelect= (symbolName) => {
    const { stage, player, game } = this.props;
    stage.set("selectedButton", symbolName);
    console.log(player);
    console.log(stage);
    console.log("stage set")
  }

  renderSubmitted() {
    return (
      <div className="task-response">
        <div className="response-submitted">
          <h5>Waiting on other players...</h5>
          Please wait until all players are ready
        </div>
      </div>
    );
  }

  renderSlider() {
    const { player } = this.props;
    const value = player.round.get("value");
    return (
      <Slider
        min={0}
        max={1}
        stepSize={0.01}
        labelStepSize={0.25}
        onChange={this.handleChange}
        value={value}
        hideHandleOnEmpty
      />
    );
  }

  renderButton() {
    const { stage, player, game } = this.props;
    const task = stage.get("task");

    return (
      task.symbols.map((symbol) => 
      <SymbolButton 
        key={symbol}
        name={symbol}
        handleButtonSelect={() => this.handleButtonSelect}
        stage={stage}
        game={game}
        player={player}
      />
      )
    );
  }

  render() {
    const { stage, player, game } = this.props;

    const task = stage.get("task");

    // If the player already submitted, don't show the slider or submit button
    if (player.stage.submitted) {
      return this.renderSubmitted();
    }

    return (
      <div className="task-response">
         {this.renderButton()}
        <form onSubmit={this.handleSubmit}>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}
