import React from "react";
import '../../../public/css/intro.css';

import { Centered } from "meteor/empirica:core";

const Radio = ({ selected, name, value, label, onChange }) => (
    <label className="questionnaire-radio">
        <input
        className="quiz-button"
        type="radio"
        name={name}
        value={value}
        checked={selected === value}
        onChange={onChange}
        />
        {label}
    </label>
);

export default class AllQuiz extends React.Component {
  state = { q1: "", q2: "", q1: "", q2: ""};

  handleChange = event => {
    const el = event.currentTarget;
    this.setState({ [el.name]: el.value });
  };

  handleSubmit = event => {
    const { hasPrev, hasNext, onNext, onPrev, game, player } = this.props;
    event.preventDefault();
    if (this.state.response === 'yes') {
        onNext();
    } else {
        player.exit("failedQuestion");
    }
  };

  render() {
    const { player } = this.props;
    const { q1, q2, q3, q4, q5, q6, q7, q8 } = this.state;

    const allSelected = Object.keys(this.state).every(key => this.state[key] !== "")
    // console.log(allSelected);
    return (
      <Centered>
        <div className="intro-heading questionnaire-heading"> Questionnaire </div>
            <div className="questionnaire-content-container">
                <div className="questionnaire-body">
                    <div className="question-section">
                        <label className="questionnaire-question">Are you willing to participate in an online team exercise that could last for approximately 60 minutes?</label>
                        <Radio
                            selected={q1}
                            name="q1"
                            value="yes"
                            label="Yes"
                            onChange={this.handleChange}
                        />
                        <Radio
                            selected={q1}
                            name="q1"
                            value="no"
                            label="No"
                            onChange={this.handleChange}
                        />
                    </div>

                    <div className="question-section">
                    <label className="questionnaire-question">If you do not interact with the application for 5 minutes your session will timeout, and the experiment will end for everyone in your team. 1 minute before the timeout you will be notified you are about to timeout, and be given a chance to reset this timer. If you allow your session to timeout your HIT will not be accepted. If a team member causes a timeout you will be sent to the end of challenge page, and your HIT will be accepted.</label>
                    <Radio
                        selected={q2}
                        name="q2"
                        value="yes"
                        label="PROCEED"
                        onChange={this.handleChange}
                    />
                    <Radio
                        selected={q2}
                        name="q2"
                        value="no"
                        label="DO NOT PROCEED"
                        onChange={this.handleChange}
                    />
                    </div>

                    <div className="question-section">
                    <label className="questionnaire-question">Next you will be asked questions about the instruction you just read. You need to get the answers correct in order to be accepted into the exercise. </label>
                    <Radio
                        selected={q3}
                        name="q3"
                        value="yes"
                        label="PROCEED"
                        onChange={this.handleChange}
                    />
                    <Radio
                        selected={q3}
                        name="q3"
                        value="no"
                        label="DO NOT PROCEED"
                        onChange={this.handleChange}
                    />
                    </div>

                    <div className="question-section">
                    <label className="questionnaire-question">Is the following statement true or false? If any member of my team doesn't register a guess or communicate with a colleague for five minutes, the task will end and the entire 5-person team (including myself) will be sent to the exit page of the survey.</label>
                    <Radio
                        selected={q4}
                        name="q4"
                        value="yes"
                        label="Yes"
                        onChange={this.handleChange}
                    />
                    <Radio
                        selected={q4}
                        name="q4"
                        value="no"
                        label="No"
                        onChange={this.handleChange}
                    />
                    </div>

                    <div className="question-section">
                    <label className="questionnaire-question">On any trial, how many abstract symbols will you and any member of your team have in common?</label>
                    <Radio
                        selected={q5}
                        name="q5"
                        value="one"
                        label="Only 1"
                        onChange={this.handleChange}
                    />
                    <Radio
                        selected={q5}
                        name="q5"
                        value="many"
                        label="More than 1"
                        onChange={this.handleChange}
                    />
                    </div>

                    <div className="question-section">
                    <label className="questionnaire-question">Is the following statement true or false? I will be able to communicate with my team members using a group chat and I will always be able to communicate with every member of my team.</label>
                    <Radio
                        selected={q6}
                        name="q6"
                        value="true"
                        label="True"
                        onChange={this.handleChange}
                    />
                    <Radio
                        selected={q6}
                        name="q6"
                        value="false"
                        label="False"
                        onChange={this.handleChange}
                    />
                    </div>

                    <div className="question-section">
                    <label className="questionnaire-question">The reconsider button will only appear if I submit an incorrect answer.</label>
                    <Radio
                        selected={q7}
                        name="q7"
                        value="true"
                        label="True"
                        onChange={this.handleChange}
                    />
                    <Radio
                        selected={q7}
                        name="q7"
                        value="false"
                        label="False"
                        onChange={this.handleChange}
                    />
                    </div>

                    <div className="question-section">  
                    <label className="questionnaire-question">You have been selected to participate in the task. You will receive a flat fee of $2 for participating. You will also receive $1 bonus each time your team correctly identifies the shared symbol. If you complete all 15 trials of the experiment, you could earn up to $17.</label>
                    <Radio
                        selected={q8}
                        name="q8"
                        value="yes"
                        label="PROCEED"
                        onChange={this.handleChange}
                    />
                    <Radio
                        selected={q8}
                        name="q8"
                        value="no"
                        label="DO NOT PROCEED"
                        onChange={this.handleChange}
                    />  
                    </div>                  
                </div>
                <form className="questionnaire-btn-container" onSubmit={this.handleSubmit}>
                    <button 
                        className={!allSelected ? "arrow-button button-submit-disabled" : "arrow-button button-submit"}
                        disabled={!allSelected} type="submit"> Submit </button> 
                </form>
            </div>
      </Centered>
    );
  }
}
