import React from "react";
import '../../public/css/intro.css'

import { Centered } from "meteor/empirica:core";

const Radio = ({ selected, name, value, label, onChange }) => (
  <label>
    <input
      type="radio"
      name={name}
      value={value}
      checked={selected === value}
      onChange={onChange}
    />
    {label}
  </label>
);

export default class QuizOne extends React.Component {
  state = { age: "", gender: "", strength: "", fair: "", feedback: "" };

  handleChange = event => {
    const el = event.currentTarget;
    this.setState({ [el.name]: el.value });
  };

  handleSubmit = event => {
    event.preventDefault();
    this.props.onSubmit(this.state);
  };

  render() {
    const { player } = this.props;
    const { age, gender, strength, fair, feedback, response } = this.state;

    return (
      <Centered>
        <div className="exit-survey">
          <h1> Questionnaire </h1>
            <div>
              <label>Are you willing to participate in an online team exercise that could last for approximately 60 minutes?</label>
              <div>
                <Radio
                  selected={response}
                  name="question"
                  value="yes"
                  label="Yes"
                  onChange={this.handleChange}
                />
                <Radio
                  selected={response}
                  name="question"
                  value="no"
                  label="No"
                  onChange={this.handleChange}
                />
              </div>
            </div>
        </div>
      </Centered>
    );
  }
}
