import React from "react";
import Slider from "meteor/empirica:slider";

import SymbolButton from "./SymbolButton.jsx";

export default class TaskResponse extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      submitted: this.props.player.submitted,
    }
  }

  handleSubmit = event => {
    const { stage, player, game } = this.props;

    event.preventDefault();
    console.log("submitted");
    player.set("submitted", true);
    this.setState({submitted: true});
  };

  handleReconsider = event => {
    const { stage, player, game } = this.props;

    event.preventDefault();
    console.log("Reconsidering...");
    console.log("BEFORE");
    console.log(player);
    // player.stage.set("submitted", false);
    player.set("submitted", false);
    this.setState({submitted: false});
    console.log("AFTER");
    console.log(player);
  }

  handleButtonSelect= (symbolName) => {
    const { stage, player, game } = this.props;
    stage.set("selectedButton", symbolName);
    console.log("PLAYER");
    console.log(player);
    console.log("STAGE");
    console.log(stage);
    console.log("stage set")
  }

  renderSubmitted() {
    return (
      <div className="task-response">
        <div className="response-submitted">
          <h5>Waiting on other players...</h5>
          Please wait until all players are ready. If you would like to reconsider your answer, click on the reconsider button.
        </div>
      </div>
    );
  }

  renderSlider() {
    const { player } = this.props;
    const value = player.round.get("value");
    return (
      <Slider
        min={0}
        max={1}
        stepSize={0.01}
        labelStepSize={0.25}
        onChange={this.handleChange}
        value={value}
        hideHandleOnEmpty
      />
    );
  }

  renderButton() {
    const { stage, player, game } = this.props;
    const task = stage.get("task");

    return (
      task.symbols.map((symbol) => 
      <SymbolButton 
        key={symbol}
        name={symbol}
        handleButtonSelect={(symbolName) => this.handleButtonSelect(symbolName)}
        stage={stage}
        game={game}
        player={player}
      />
      )
    );
  }

  render() {
    const { stage, player, game, handleReconsider } = this.props;

    const submitted = player.get("submitted");
    // If the player already submitted, don't show the slider or submit button
    console.log(submitted);

    const disabled = player.get("submitted");
    return (
      <div className="task-response">
        {submitted ? this.renderSubmitted() : ""}
        {this.renderButton()}
        <form onSubmit={this.handleSubmit}>
          {submitted ? 
          <button disabled type="submit">Submit</button> :
          <button type="submit">Submit</button>
          }
        </form>
        { !submitted ?
        <button disbaled> Reconsider </button> : 
        <button onClick={handleReconsider}> Reconsider </button>
        }
      </div>
    );
  }
}
